import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import MainLayout from './pages/MainLayout';

function getToken() {
  return localStorage.getItem('token') || sessionStorage.getItem('token');
}

function App() {
  const [isAuth, setIsAuth] = React.useState(!!getToken());

  // Следим за изменением токена (выход/вход)
  React.useEffect(() => {
    const syncAuth = () => setIsAuth(!!getToken());
    window.addEventListener('storage', syncAuth);
    return () => window.removeEventListener('storage', syncAuth);
  }, []);

  // Принудительно обновляем авторизацию при каждом монтировании
  React.useEffect(() => {
    setIsAuth(!!getToken());
  });

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/*" element={isAuth ? <MainLayout /> : <Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
