import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import MainLayout from './pages/MainLayout';

function App() {
  const [authState, setAuthState] = React.useState(() => !!(localStorage.getItem('token') || sessionStorage.getItem('token')));
  React.useEffect(() => {
    const syncAuth = () => setAuthState(!!(localStorage.getItem('token') || sessionStorage.getItem('token')));
    window.addEventListener('storage', syncAuth);
    return () => window.removeEventListener('storage', syncAuth);
  }, []);
  const isAuth = authState;
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/*" element={isAuth ? <MainLayout /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
