import React, { useState } from 'react';
// import axios from 'axios'; // подключим позже

function AddAccountModal({ open, onClose }) {
  const [step, setStep] = useState(1);
  const [phone, setPhone] = useState('');
  const [apiId, setApiId] = useState('');
  const [apiHash, setApiHash] = useState('');
  const [code, setCode] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [needPassword, setNeedPassword] = useState(false);
  const [loading, setLoading] = useState(false);

  const reset = () => {
    setStep(1);
    setPhone('');
    setApiId('');
    setApiHash('');
    setCode('');
    setPassword('');
    setError('');
    setNeedPassword(false);
    setLoading(false);
  };

  const handleSendCode = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    // TODO: отправить запрос на backend /api/telegram/send_code
    // Пример:
    // const res = await axios.post('/api/telegram/send_code', { phone, apiId, apiHash });
    setTimeout(() => {
      setLoading(false);
      setStep(2);
    }, 800); // имитация запроса
  };

  const handleVerifyCode = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    // TODO: отправить запрос на backend /api/telegram/verify_code
    // const res = await axios.post('/api/telegram/verify_code', { phone, code });
    setTimeout(() => {
      setLoading(false);
      // Пример: если нужен пароль, показать поле для пароля
      setNeedPassword(true);
      setStep(3);
    }, 800);
  };

  const handleFinish = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    // TODO: отправить финальный запрос с паролем
    // await axios.post(...)
    setTimeout(() => {
      setLoading(false);
      onClose();
      reset();
    }, 800);
  };

  const handleClose = () => {
    onClose();
    reset();
  };

  if (!open) return null;

  return (
    <div className="tg-modal-overlay">
      <div className="tg-modal">
        <h3>Добавить Telegram-аккаунт</h3>
        {step === 1 && (
          <form onSubmit={handleSendCode}>
            <input
              type="text"
              placeholder="Номер телефона"
              value={phone}
              onChange={e => setPhone(e.target.value)}
              required
              disabled={loading}
            />
            <input
              type="text"
              placeholder="API ID"
              value={apiId}
              onChange={e => setApiId(e.target.value)}
              required
              disabled={loading}
            />
            <input
              type="text"
              placeholder="API Hash"
              value={apiHash}
              onChange={e => setApiHash(e.target.value)}
              required
              disabled={loading}
            />
            <div className="tg-modal-actions">
              <button type="submit" disabled={loading}>Далее</button>
              <button type="button" onClick={handleClose}>Отмена</button>
            </div>
            {error && <div className="auth-error">{error}</div>}
          </form>
        )}
        {step === 2 && (
          <form onSubmit={handleVerifyCode}>
            <input
              type="text"
              placeholder="Код подтверждения"
              value={code}
              onChange={e => setCode(e.target.value)}
              required
              disabled={loading}
            />
            <div className="tg-modal-actions">
              <button type="submit" disabled={loading}>Далее</button>
              <button type="button" onClick={() => setStep(1)} disabled={loading}>Назад</button>
            </div>
            {error && <div className="auth-error">{error}</div>}
          </form>
        )}
        {step === 3 && needPassword && (
          <form onSubmit={handleFinish}>
            <input
              type="password"
              placeholder="Пароль (если требуется)"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              disabled={loading}
            />
            <div className="tg-modal-actions">
              <button type="submit" disabled={loading}>Завершить</button>
              <button type="button" onClick={() => setStep(2)} disabled={loading}>Назад</button>
            </div>
            {error && <div className="auth-error">{error}</div>}
          </form>
        )}
      </div>
    </div>
  );
}

export default AddAccountModal;
