import React, { useState } from 'react';

function AddAccountModal({ open, onClose }) {
  const [phone, setPhone] = useState('');
  const [apiId, setApiId] = useState('');
  const [apiHash, setApiHash] = useState('');
  const [code, setCode] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: отправка данных на backend
    onClose();
  };

  if (!open) return null;

  return (
    <div className="tg-modal-overlay">
      <div className="tg-modal">
        <h3>Добавить Telegram-аккаунт</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Номер телефона"
            value={phone}
            onChange={e => setPhone(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="API ID"
            value={apiId}
            onChange={e => setApiId(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="API Hash"
            value={apiHash}
            onChange={e => setApiHash(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Код подтверждения"
            value={code}
            onChange={e => {
              setCode(e.target.value);
              if (e.target.value.length > 0) setShowPassword(true);
            }}
            required
          />
          {showPassword && (
            <input
              type="password"
              placeholder="Пароль (если требуется)"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          )}
          <div className="tg-modal-actions">
            <button type="submit">Добавить</button>
            <button type="button" onClick={onClose}>Отмена</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddAccountModal;
